import React, { Component } from 'react';
import Web3 from 'web3';
import './App.css';
import Navbar from './Navbar';
import Token from '../abis/Token.json'
import ethSwap from '../abis/ethSwap.json'

class App extends Component {

  async componentWillMount() {
    await this.loadWeb3()
    await this.loadBlockchainData()
  }

  async loadBlockchainData() {
    const web3 = window.web3
    const accounts = await web3.eth.getAccounts()
    //console.log(accounts[0])
    this.setState({account : accounts[0]})
    //console.log(this.state.account)
    const ethBalance = await web3.eth.getBalance(this.state.account)
    this.setState({ethBalance}) //ES6 - {ethBalance : ethBalance}
    //console.log(this.state.ethBalance)

    /*
    const abi = Token.abi
    const networkId = await web3.eth.net.getId() //returns 5777 as we are connected to ganache
    //const address = Token.networks['5777'].address
    const address = Token.networks[networkId]
    const token = new web3.eth.Contract(abi, address)
    console.log(token)
    */

  //Load Token
   const networkId = await web3.eth.net.getId() //returns 5777 as we are connected to ganache
   const tokenData = Token.networks[networkId]
   if(tokenData){
    const token = new web3.eth.Contract(Token.abi, tokenData.address)
    //console.log(token)
    this.setState({token}) //{token : token}
    let tokenBalance = await token.methods.balanceOf(this.state.account).call()
   } else {
    window.alert('Token Contract not deployed to detected network')
   }
 }


  async loadWeb3() {
    if (window.ethereum) {
    window.web3 = new Web3(window.ethereum)
    await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
    }
  }

  constructor(props) {
    super(props)
    this.state = {
      account : '',
      token : {}, //empty object
      ethBalance : '0'
    }
  }


  render() {
    return (
      <div>
        <Navbar account = {this.state.account}/> 
        <div className="container-fluid mt-5">
          <div className="row">
            <main role="main" className="col-lg-12 d-flex text-center">
              <div className="content mr-auto ml-auto">
                <a
                  href="http://www.dappuniversity.com/bootcamp"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                </a>
                <h1>Hello</h1>
              </div>
            </main>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
